import React from "react";

class WritingPlatform extends React.Component{
    constructor(){
        super();
        this.state={};
    }

    componentDidMount(){
        this.setState({quill: new Quill ('#editor', { theme: 'snow'})})
    }

    render(){
        return(
            <div id="editor">
                <p>write here</p>
            </div>
        );
    }
}

export default WritingPlatform;
