import React from "react";
import { Link } from "react-router-dom";
import DashboardNavBar from "../components/DashboardNavBar.js";
import SideBar from "../components/Sidebar.js"
import Documents from "../components/Documents.js";
import File from "../components/File.js"
import "../styles/dashboard.css";
import Files from "../files.json";

class Dashboard extends React.Component{
    constructor(){
        super();
        this.state={
            active: false,
            filesLoaded: false 
        }

        this.sidebarCollapse = this.sidebarCollapse.bind(this);
    }

    sidebarCollapse(){
        const active = !this.state.active;
        this.setState({
            active: active
        })
    }

    /**perform a query to the database
     * when this component is mounted
     */
    componentDidMount(){
        setTimeout(  
            () => {
                this.setState({
                    files: Files,
                    filesLoaded: true
                })
            }    
        ,500);
    }

    render(){
        /**Create an array holding all File functional components to be rendered.*/
        const FilesCollection =  Files.map( file => 
                                    <File 
                                        key={file.id}
                                        id={file.id} 
                                        title={file.title}  
                                        author={file.author}
                                        lastModified={file.lastModified}
                                        filesLoaded={this.state.filesLoaded}
                                    />);

        return(
            <div className="dashboard-container">
                <DashboardNavBar sidebarCollapse={this.sidebarCollapse}/>
                <div id="sidebar-doc-container">
                    {/** */}
                    <SideBar active={this.state.active} />
                    
                    {/**Pass the FilesCollection to the Documents component to render*/}
                    <Documents filesCollection={FilesCollection} />
                </div>
            </div>
        );
    }
}

export default Dashboard;
