import React from "react";

class HPShowOffProduct extends React.Component{
    constructor(){
        super();
        this.state={
            renderPharses: true,
            defaultPhrase: 'Welcome to Virtual Version',
            phrases:['Developer', 'Designer', 'Creator']
        }
    
    }

    componentDidMount(){
        window.addEventListener('scroll', this.handleScroll);
    }

    handleScroll= () =>{
        const { phrases } = this.state;
        
        for( let i = 0; i < phrases.length; i++){
            for(let j = 0; j < phrases[i].length; i++){
                console.log(phrases[j].charAt(0));
            }
        }
    }

    
    render(){
        return(
            <div id="show-off-product-container" >
                <div id="sell-product-phrases-container">
                    {/* We can add custom attributes to jsx tags 
                    we can make these extra attribute using the key word 
                    data. Place a dash after the keyword data then state 
                    your variable name. We can access these custom attributes
                    with js.
                    */}
                    <div id="type-writer">
                        Willy Alicon The    
                        <span className="text-type"></span>
                    </div>
                </div>
                
                <div id="slide-show">
    
                </div>
            </div>
        );
    }    
}

export default HPShowOffProduct;
